@mixin inputVariables($variable) {
    @if $variable == border {
        border: 2px solid #696e79;
    }
    @else if $variable == invalidBorder {
        border: 2px solid red;
    }
    @else if $variable == inputColor {
        color: #545550;
    }
    @else if $variable == backgroundColor {
        background-color: #191e29;
    }
    @else if $variable == inputBackground {
        background-color: #191e29;
    }
}
form {
    color: white;
    .fa {
        height: 10px;
        width: 10px;
        position: absolute;
        right: 9em;
        top: -.2em;       
        @include inputVariables(inputColor);
        font-size: 1em;
    }
    button {
        background-color: #ac8e6a;
        color: white;
        border: none;
        border-radius: 24px;
        -webkit-border-radius: 24px;
        -moz-border-radius: 24px;
        -ms-border-radius: 24px;
        -o-border-radius: 24px;
        padding: .6em 0;
        font-weight: 700;
        letter-spacing: .018em;
    }
    ::placeholder {
        @include inputVariables(inputColor);
    }
}


input, textarea {
    &:focus {
        outline: none;
        @include inputVariables(border);
        border-color: white !important;
        color: white !important;
        background-color: black !important;
    }
    // &:hover {}
    border-radius: 4px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    -ms-border-radius: 4px;
    -o-border-radius: 4px;
    @include inputVariables(border);
    margin: .5em .2em;
    padding: .2em .5em;
    @include inputVariables(inputBackground);
    transition: all .5s ease-in-out;
    -webkit-transition: all .5s ease-in-out;
    -moz-transition: all .5s ease-in-out;
    -ms-transition: all .5s ease-in-out;
    -o-transition: all .5s ease-in-out;
}


